import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import openHAB from "../../openHAB/openHAB";

const Login = () => {
  const [usernameValue, setUsernameValue] = useState('');
  const [passwordValue, setPasswordValue] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();

  // Initialize a boolean state
  const [passwordShown, setPasswordShown] = useState(false);

  // Password toggle handler
  const togglePassword = () => {
    setPasswordShown(!passwordShown);
  };

  useEffect(() => {
    sessionStorage.removeItem('auth');
    document.title = "SmartRoom – Login";
  }, []);

  const authenticateUser = async (e, usernameValue, passwordValue) => {
    e.preventDefault();

    try {
      const response = await fetch(openHAB.url, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email: usernameValue,
          password: passwordValue,
        }),
      });

      if (response.ok) {
        const data = await response.json();
        sessionStorage.setItem('auth', 'true');
        const accessToken = data.user.access;
        // Now you can use the accessToken as needed (e.g., store it in state)

        navigate('/overview');
      } else {
        setErrorMessage('Wrong username or password, please try again');
      }
    } catch (error) {
      console.error('Error during authentication:', error);
      setErrorMessage('An error occurred during authentication');
    }
  };

  return (
    <div className="login-container">
      <img
        src="smart-room-logo-orange.svg"
        width="130"
        height="130"
        className="center logo"
        alt="SmartRoom Logo"
      />
      <h1 className="login-header">SmartRoom</h1>
      <form name="login-form">
        {/* ... (rest of your form inputs) */}

        {errorMessage && (
          <div className="text-danger form-control input-error">{errorMessage}</div>
        )}

        <label className="form-control form-control-input-password">
          <input
            type="checkbox"
            onClick={togglePassword}
            className="checkbox-icon"
          />
          Show password
        </label>
        <button
          type="submit"
          className="form-control btn-primary"
          onClick={(e) => authenticateUser(e, usernameValue, passwordValue)}
        >
          Log in
        </button>
      </form>
    </div>
  );
};

export default Login;
